(62, 'Cuộc đời cư sĩ Viên Hoàng', 'YLZ0pWRQRB4&list=PL4B2966B2077102D7', 0, '', 6, '2015-02-26 22:52:25', 0, 1, 'vi', '0000-00-00'),
(63, 'Đại đường tây vực ký', 'csvs7LPjghI&list=PLA48C8302E9B7DBC6', 0, '', 6, '2015-02-26 22:53:50', 0, 1, 'vi', '0000-00-00'),
(64, 'Đạt ma tổ sư', '1LZBu7h2RIo', 0, '', 6, '2015-02-26 22:56:04', 0, 1, 'vi', '0000-00-00'),
(65, 'Giải oan', 'Egfj98TUNlY', 0, 'Phim kể về một phụ nữ nhiều kiếp trước hành nghề giết mổ bò. Những con bò bị giết ôm hận thù, nên ở các kiếp sau luôn tìm người nữ đó trả thù, gây cho chị nhiều căn bệnh đau đớn. Nhờ được một đại sư đến trì niệm Phật, những con bò đó đã bớt đau đớn và vãnh sanh, chị không còn bị chúng đòi mạng nữa\r\n', 6, '2015-04-12 11:47:48', 0, 1, 'vi', '1970-01-01'),
(66, 'Giám chân đông độ', 'qNqlZcyLLKA&list=PLfaYuVSAGjswX5-Q0ryQ-m3fGsRnNIaq8', 0, '', 6, '2015-02-26 22:57:51', 0, 1, 'vi', '0000-00-00'),
(67, 'Giả sử ngày mai đến', 'R2RKB6ic0b4', 0, '', 6, '2015-02-26 23:00:08', 0, 1, 'vi', '0000-00-00'),
(68, 'Hoà thượng Hư Vân', 'sLawYPR_aSI&list=PLk_dCDO3joVwOR8_NVww6YZDthL5g_ye9', 0, '', 6, '2015-02-26 23:02:42', 0, 1, 'vi', '0000-00-00'),
(69, 'Hổ ly sơn thất thế', '0Qt0hBKhQsk', 0, 'Hổ Ly Sơn Thất Thế là bộ phim tâm lý Phật giáo xoay quanh chủ đề sự mong manh vô thường của kiếp sống, đặc tả sức mạnh đoàn kết của đời sống lục hòa thanh tịnh. Phim còn cho thấy mọi quyết định rẽ hướng mà không có cơ sở sẽ dẫn đến những hậu quả nguy hiểm, đồng thời phim cũng mang đến thông điệp cho người con Phật về tinh thần tôn sư trọng đạo, không vượt qua lễ nghi giới luật khi còn là người học trò thiếu kinh nghệm, dễ sa vào cạm bẫy của cuộc đời.', 8, '2015-04-12 14:03:06', 0, 1, 'vi', '1970-01-01'),
(70, 'Liễu phàm tứ huấn', 'tGbvxKOcaNE', 0, '', 6, '2015-02-26 23:06:26', 0, 1, 'vi', '0000-00-00'),
(71, 'Lục tổ Huệ Năng', 'u9YH8HYXoWg', 0, '', 6, '2015-02-26 23:07:29', 0, 1, 'vi', '0000-00-00'),
(72, 'Nghịch duyên', 'HSyEG2ziuEc', 0, 'Phim kể về một người phụ nữ, dù sống trong cảnh khó khăn để cầu đạo, bị chồng cấm ngặt mọi chuyện liên quan đến Phật: không cho ăn chay, không cho niệm Phật. Chị phải cùng chồng đi giết mổ heo, nhưng mỗi khi giết, chị đều niệm Phật gia hộ cho những chú heo trở về cõi Cực Lạc. Cuối cùng, mọi hoàn cảnh vẫn không thể làm chị thối chí trì niệm danh hiệu A Di Đà, và chị đã được đến Tây Phương.\r\n', 6, '2015-04-12 12:01:45', 0, 1, 'vi', '1970-01-01'),
(73, 'Nghịch tử', '4i9B-jLFr6Y', 0, '', 8, '2015-02-26 23:20:28', 0, 1, 'vi', '1970-01-01'),
(74, 'Oan hồn đòi mạng', 'sgHh8tEk4EQ', 0, '', 6, '2015-02-26 23:12:21', 0, 1, 'vi', '0000-00-00'),
(75, 'Quan Âm bán cá', 'Q_Rd287IZ9o', 0, '', 6, '2015-02-26 23:13:19', 0, 1, 'vi', '0000-00-00'),
(76, 'Suốt chặng đường đời', 'zzOii48sARc&list=PL7TBQBxWfXoPV3UGWs1Dm9vGlEeBS2tMD', 0, '', 6, '2015-02-26 23:15:52', 0, 1, 'vi', '0000-00-00'),
(77, 'Thập thiện nghiệp', 'ijKY4SpHVtI', 0, '', 8, '2015-02-26 23:20:16', 0, 1, 'vi', '1970-01-01'),
(78, 'Tỉnh mộng', 'pVkdZ7w_GtE', 0, '', 8, '2015-02-26 23:20:09', 0, 1, 'vi', '1970-01-01'),
(79, 'Trở lại đường xưa', 'kVxb0jf91V0', 0, 'Chuyện kể một anh chàng thường hay quậy phá, trộm cắp của cải trong làng. Được một vị chân Sư khai ngộ, cảnh tỉnh, tâm thức anh dần thay đổi. Một hôm,  anh đến nhà của một người giàu để trộm, trong nhà có một cô gái và bà mẹ đang bệnh nặng. Cô gái một mực bảo anh là người tốt đã đến cứu mẹ cô, anh lúng túng chẳng biết làm sao, chợt nhớ lời thầy dạy "chỉ cần niệm danh hiệu Quan Thế Âm thành tâm thì sẽ được cứu", cô gái đem hương hoa đèn đến trước nhà, thắp hương cầu nguyện. Một vị thầy thuốc đi ngang vào cứu giúp. Bà lão thoát chết, sai cô con gái đem một món tiền lớn cho chàng trộm, nhưng giờ chàng đã tỉnh ngộ nên từ chối và bảo "hãy phân phát đến những người bất hạnh trong làng". Từ đó anh thay đổi mình, trở thành người tốt bụng, thường lành việc thiện và thực hành niệm Phật', 8, '2015-04-12 12:28:11', 0, 1, 'vi', '1970-01-01'),
(80, 'Nhất hưu hòa thượng', 'GwYthzGrUdQ&list=PLB35B0B7201AE78CE', 0, '', 9, '2015-02-26 23:26:30', 0, 1, 'vi', '0000-00-00'),
